              </div>
              <div className="flex justify-between">
                <span>Shipping</span>
                <span>â‚¹{shipping.toLocaleString('en-IN')}</span>
              </div>
              <div className="flex justify-between">
                <span>Tax (GST 18%)</span>
                <span>â‚¹{tax.toLocaleString('en-IN')}</span>
              </div>
              <div className="border-t pt-2 flex justify-between text-lg font-medium">
                <span>Total</span>
                <span>â‚¹{finalTotal.toLocaleString('en-IN')}</span>
              </div>
            </div>

            <div className="mt-6 text-sm text-gray-600">
              <p>â€¢ Free returns within 30 days</p>
              <p>â€¢ 2-year warranty included</p>
              <p>â€¢ Delivery in 3-5 business days</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

