import { useState, useEffect } from 'react';
import { useParams, useLocation } from 'wouter';
import { useQuery } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { useCartStore } from '@/lib/cart';
import { useToast } from '@/hooks/use-toast';
import { Minus, Plus, Heart, Share, ArrowLeft, Truck, Shield, RotateCcw, Star, ChevronLeft, ChevronRight } from 'lucide-react';
import { Link } from 'wouter';
import type { ProductWithCategory } from '@shared/schema';
import VirtualTryOn from '@/components/virtual-tryon';

export default function ProductPage() {
  const { slug } = useParams<{ slug: string }>();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const { addItem } = useCartStore();

  const [selectedImage, setSelectedImage] = useState(0);
  const [selectedSize, setSelectedSize] = useState('');
  const [selectedColor, setSelectedColor] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [isWishlisted, setIsWishlisted] = useState(false);

  const { data: product, isLoading, error } = useQuery({
    queryKey: ['/api/products', slug],
    enabled: !!slug,
  }) as { data: ProductWithCategory | undefined; isLoading: boolean; error: any };

  // Auto-select first available options
  useEffect(() => {
    if (product && !selectedSize && product.sizes && product.sizes.length > 0) {
      setSelectedSize(product.sizes[0]);
    }
    if (product && !selectedColor && product.colors && product.colors.length > 0) {
      setSelectedColor(product.colors[0]);
    }
  }, [product, selectedSize, selectedColor]);

  const handleAddToCart = () => {
    if (!product) return;

    const cartItem = {
      id: `${product.id}-${selectedSize}-${selectedColor}-${Date.now()}`,
      productId: product.id,
      quantity,
      size: selectedSize || null,
      color: selectedColor || null,
      product: product,
      sessionId: 'session'
    };

    addItem(cartItem);

    toast({
      title: "Added to cart",
      description: `${product.name} has been added to your cart.`,
    });
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-white">
        <div className="container mx-auto px-6 py-20">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
            {/* Image Skeleton */}
            <div className="space-y-4">
              <div className="aspect-square bg-gray-200 animate-pulse rounded-lg" />
              <div className="grid grid-cols-4 gap-2">
                {Array.from({ length: 4 }).map((_, i) => (
                  <div key={i} className="aspect-square bg-gray-200 animate-pulse rounded" />
                ))}
              </div>
            </div>
            {/* Content Skeleton */}
            <div className="space-y-6">
              <div className="h-8 bg-gray-200 animate-pulse rounded" />
              <div className="h-6 bg-gray-200 animate-pulse rounded w-1/3" />
              <div className="space-y-2">
                <div className="h-4 bg-gray-200 animate-pulse rounded" />
                <div className="h-4 bg-gray-200 animate-pulse rounded w-3/4" />
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error || !product) {
    return (
      <div className="min-h-screen bg-white flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-light tracking-wide mb-4">Product not found</h1>
          <Link href="/">
            <Button className="bg-black text-white px-8 py-3 font-light tracking-wide">
              Return Home
            </Button>
          </Link>
        </div>
      </div>
    );
  }

  const productImages = product.images && product.images.length > 0 
    ? product.images 
    : ['https://images.unsplash.com/photo-1441986300917-64674bd600d8?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&h=800'];

  return (
    <div className="min-h-screen bg-white">
      <div className="grid grid-cols-1 lg:grid-cols-2 min-h-screen">
        {/* Left Side - Product Images */}
        <div className="relative bg-gray-50">
          {/* Main Image */}
          <div className="h-screen sticky top-0 overflow-hidden">
            <img
              src={productImages[selectedImage]}
              alt={product.name}
              className="w-full h-full object-cover"
              data-testid="img-product-main"
            />

            {/* Image Navigation */}
            {productImages.length > 1 && (
              <>
                <button
                  onClick={() => setSelectedImage(selectedImage > 0 ? selectedImage - 1 : productImages.length - 1)}
                  className="absolute left-4 top-1/2 -translate-y-1/2 w-10 h-10 bg-white/80 backdrop-blur-sm rounded-full flex items-center justify-center hover:bg-white transition-colors"
                >
                  <ChevronLeft size={20} />
                </button>
                <button
                  onClick={() => setSelectedImage(selectedImage < productImages.length - 1 ? selectedImage + 1 : 0)}
                  className="absolute right-4 top-1/2 -translate-y-1/2 w-10 h-10 bg-white/80 backdrop-blur-sm rounded-full flex items-center justify-center hover:bg-white transition-colors"
                >
                  <ChevronRight size={20} />
                </button>
              </>
            )}

            {/* Image Dots Indicator */}
            {productImages.length > 1 && (
              <div className="absolute bottom-6 left-1/2 -translate-x-1/2 flex space-x-2">
                {productImages.map((_: string, index: number) => (
                  <button
                    key={index}
                    onClick={() => setSelectedImage(index)}
                    className={`w-2 h-2 rounded-full transition-colors ${
                      selectedImage === index ? 'bg-black' : 'bg-white/60'
                    }`}
                  />
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Right Side - Product Details */}
        <div className="px-8 lg:px-12 py-12 flex flex-col justify-between min-h-screen">
          {/* Back Button */}
          <div className="mb-8">
            <Link href="/">
              <Button variant="ghost" size="sm" className="p-0 h-auto font-light text-sm tracking-wide hover:bg-transparent">
                â† BACK
              </Button>
            </Link>
          </div>

          {/* Product Details */}
          <div className="flex-1 space-y-8">
            {/* Product Header */}
            <div className="space-y-6">
              <h1 className="text-2xl md:text-3xl font-light tracking-[0.05em] leading-tight" data-testid="text-product-name">
                {product.name}
              </h1>
              <p className="text-xl font-light text-gray-900" data-testid="text-product-price">
                â‚¹{parseFloat(product.price).toLocaleString('en-IN')}
              </p>
            </div>

            {/* Product Description */}
            {product.description && (
              <div className="border-t border-gray-200 pt-6">
                <p className="text-sm text-gray-600 leading-relaxed font-light" data-testid="text-product-description">
                  {product.description}
                </p>
              </div>
            )}

            {/* Product Options */}
            <div className="space-y-8">
              {/* Size Selection */}
              {product.sizes && product.sizes.length > 0 && (
                <div>
                  <h3 className="text-xs font-light tracking-[0.2em] uppercase mb-4 text-gray-700">
                    Size
                  </h3>
                  <div className="grid grid-cols-4 gap-2">
                    {product.sizes.map((size) => (
                      <button
                        key={size}
                        onClick={() => setSelectedSize(size)}
                        className={`py-3 text-xs font-light tracking-wide transition-all duration-200 border ${
                          selectedSize === size
                            ? 'border-black bg-black text-white'
                            : 'border-gray-300 hover:border-black'
                        }`}
                        data-testid={`button-size-${size.toLowerCase()}`}
                      >
                        {size}
                      </button>
                    ))}
                  </div>
                </div>
              )}

              {/* Color Selection */}
              {product.colors && product.colors.length > 0 && (
                <div>
                  <h3 className="text-xs font-light tracking-[0.2em] uppercase mb-4 text-gray-700">
                    Color
                  </h3>
                  <div className="flex flex-wrap gap-2">
                    {product.colors.map((color) => (
                      <button
                        key={color}
                        onClick={() => setSelectedColor(color)}
                        className={`px-4 py-2 text-xs font-light tracking-wide transition-all duration-200 border ${
                          selectedColor === color
                            ? 'border-black bg-black text-white'
                            : 'border-gray-300 hover:border-black'
                        }`}
                        data-testid={`button-color-${color.toLowerCase()}`}
                      >
                        {color}
                      </button>
                    ))}
                  </div>
                </div>
              )}
            </div>

          </div>

          {/* Bottom Section - Actions */}
          <div className="space-y-6 border-t border-gray-200 pt-8">
            {/* Quantity Selector */}
            <div className="flex items-center justify-between">
              <span className="text-xs font-light tracking-[0.2em] uppercase text-gray-700">Quantity</span>
              <div className="flex items-center border border-gray-300">
                <button
                  onClick={() => setQuantity(Math.max(1, quantity - 1))}
                  disabled={quantity <= 1}
                  className="p-2 hover:bg-gray-50 disabled:opacity-50"
                  data-testid="button-decrease-quantity"
                >
                  <Minus size={14} />
                </button>
                <span className="px-4 py-2 border-x border-gray-300 min-w-[50px] text-center text-sm" data-testid="text-quantity">
                  {quantity}
                </span>
                <button
                  onClick={() => setQuantity(quantity + 1)}
                  className="p-2 hover:bg-gray-50"
                  data-testid="button-increase-quantity"
                >
                  <Plus size={14} />
                </button>
              </div>
            </div>

            {/* Action Buttons */}
            <div className="space-y-3">
              <Button
                onClick={handleAddToCart}
                className="w-full py-4 bg-black text-white font-light tracking-[0.1em] text-xs uppercase hover:bg-gray-800 transition-colors"
                data-testid="button-add-to-cart"
              >
                Add to Bag
              </Button>

              <div className="space-y-3">
                {/* Virtual Try-On */}
                <VirtualTryOn 
                  productImage={productImages[0]}
                  productName={product.name}
                />

                <div className="flex space-x-3">
                  <Button
                    variant="outline"
                    onClick={() => setIsWishlisted(!isWishlisted)}
                    className="flex-1 py-3 font-light tracking-wide text-xs uppercase border-gray-300"
                    data-testid="button-wishlist"
                  >
                    <Heart size={14} className={`mr-2 ${isWishlisted ? 'fill-current' : ''}`} />
                    Wishlist
                  </Button>
                  <Button
                    variant="outline"
                    className="flex-1 py-3 font-light tracking-wide text-xs uppercase border-gray-300"
                    data-testid="button-share"
                  >
                    <Share size={14} className="mr-2" />
                    Share
                  </Button>
                </div>
              </div>
            </div>

            {/* Product Features */}
            <div className="text-xs text-gray-600 space-y-2 pt-4 border-t border-gray-100">
              <div className="flex items-center space-x-2">
                <Truck size={14} />
                <span>Free delivery</span>
              </div>
              <div className="flex items-center space-x-2">
                <RotateCcw size={14} />
                <span>Free returns</span>
              </div>
              <div className="flex items-center space-x-2">
                <Shield size={14} />
                <span>2-year warranty</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* You May Be Interested In Section */}
      <RecommendedProducts currentProductId={product.id} />
    </div>
  );
}

// Recommended Products Component
function RecommendedProducts({ currentProductId }: { currentProductId: string }) {
  const { data: products } = useQuery({
    queryKey: ['/api/products'],
  }) as { data: ProductWithCategory[] | undefined };

  if (!products || products.length <= 1) return null;

  // Filter out current product and take first 4
  const recommendedProducts = products
    .filter((p) => p.id !== currentProductId)
    .slice(0, 4);

  return (
    <div className="bg-gray-50 py-20">
      <div className="container mx-auto px-6">
        <h2 className="text-2xl md:text-3xl font-extralight tracking-[0.1em] text-center mb-16">
          YOU MAY BE INTERESTED IN
        </h2>

        <div className="grid grid-cols-2 lg:grid-cols-4 gap-8">
          {recommendedProducts.map((recProduct) => (
            <Link 
              key={recProduct.id} 
              href={`/product/${recProduct.slug}`}
              className="group"
              data-testid={`link-recommended-${recProduct.slug}`}
            >
              <div className="space-y-4">
                {/* Product Image */}
                <div className="aspect-[2/3] bg-white overflow-hidden">
                  <img
                    src={recProduct.images?.[0] || 'https://images.unsplash.com/photo-1441986300917-64674bd600d8?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=800'}
                    alt={recProduct.name}
                    className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-105"
                  />
                </div>

                {/* Product Info */}
                <div className="space-y-2">
                  <h3 className="text-sm font-light tracking-wide text-gray-900 group-hover:text-black transition-colors">
                    {recProduct.name}
                  </h3>
                  <p className="text-sm font-light text-gray-700">
                    â‚¹{parseFloat(recProduct.price).toLocaleString('en-IN')}
                  </p>
                </div>
              </div>
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
}

client/src/pages/product.tsx.content:
import { useParams } from 'wouter';
import { useQuery } from '@tanstack/react-query';
import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Heart, Minus, Plus, ShoppingBag, Truck, RotateCcw } from 'lucide-react';
import { useCartStore } from '@/lib/cart';
import { useToast } from '@/hooks/use-toast';
import type { ProductWithCategory } from '@shared/schema';

export default function ProductPage() {
  const { slug } = useParams();
  const [selectedSize, setSelectedSize] = useState('');
  const [selectedColor, setSelectedColor] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [liked, setLiked] = useState(false);
  const [selectedImage, setSelectedImage] = useState(0);

  const { addItem } = useCartStore();
  const { toast } = useToast();

  const { data: product, isLoading } = useQuery({
    queryKey: ['/api/products', slug],
  }) as { data: ProductWithCategory; isLoading: boolean };

  const handleAddToCart = () => {
    if (!product) return;

    if (product.sizes.length > 1 && !selectedSize) {
      toast({
        title: 'Please select a size',
        variant: 'destructive',
      });
      return;
    }

    if (product.colors.length > 1 && !selectedColor) {
      toast({
        title: 'Please select a color',
        variant: 'destructive',
      });
      return;
    }

    const cartItem = {
      id: `${product.id}-${selectedSize}-${selectedColor}`,
      sessionId: 'anonymous',
      productId: product.id,
      quantity,
      size: selectedSize || null,
      color: selectedColor || null,
      product,
    };

    addItem(cartItem);

    toast({
      title: 'Added to cart',
      description: `${product.name} has been added to your cart.`,
    });
  };

  if (isLoading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
          <div className="animate-pulse">
            <div className="bg-gray-200 aspect-square rounded-lg mb-4"></div>
            <div className="grid grid-cols-4 gap-2">
              {Array.from({ length: 4 }).map((_, i) => (
                <div key={i} className="bg-gray-200 aspect-square rounded"></div>
              ))}
            </div>
          </div>
          <div className="space-y-6 animate-pulse">
            <div className="h-8 bg-gray-200 rounded w-3/4"></div>
            <div className="h-6 bg-gray-200 rounded w-1/4"></div>
            <div className="space-y-2">
              <div className="h-4 bg-gray-200 rounded"></div>
              <div className="h-4 bg-gray-200 rounded w-5/6"></div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (!product) {
    return (
      <div className="container mx-auto px-4 py-8">
        <p className="text-center text-gray-500">Product not found.</p>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Breadcrumb */}
      <nav className="text-sm text-gray-500 mb-8" data-testid="nav-breadcrumb">
        <span>Home</span>
        <span className="mx-2">/</span>
        {product.category && (
          <>
            <span>{product.category.name}</span>
            <span className="mx-2">/</span>
          </>
        )}
        <span className="text-black font-medium">{product.name}</span>
      </nav>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
        {/* Product Images */}
        <div className="space-y-4">
          <div className="aspect-square bg-gray-50 overflow-hidden rounded-lg">
            <img
              src={product.images?.[selectedImage] || product.images?.[0] || 'https://images.unsplash.com/photo-1441986300917-64674bd600d8?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&h=800'}
              alt={product.name}
              className="w-full h-full object-cover cursor-zoom-in"
              data-testid="img-product-main"
            />
          </div>

          {product.images && product.images.length > 1 && (
            <div className="grid grid-cols-4 gap-2" data-testid="grid-product-thumbnails">
              {product.images.map((image, index) => (
                <button
                  key={index}
                  onClick={() => setSelectedImage(index)}
                  className={`aspect-square bg-gray-50 overflow-hidden rounded border-2 ${
                    selectedImage === index ? 'border-black' : 'border-transparent'
                  }`}
                  data-testid={`button-thumbnail-${index}`}
                >
                  <img
                    src={image}
                    alt={`${product.name} ${index + 1}`}
                    className="w-full h-full object-cover"
                  />
                </button>
              ))}
            </div>
          )}
        </div>

        {/* Product Details */}
        <div className="space-y-6">
          <div>
            <h1 className="text-3xl font-light mb-4 tracking-wide" data-testid="text-product-name">
              {product.name}
            </h1>
            <p className="text-2xl font-medium" data-testid="text-product-price">
              â‚¹{parseFloat(product.price).toLocaleString('en-IN', {
                minimumFractionDigits: 2,
              })}
            </p>
          </div>

          {product.description && (
            <p className="text-gray-600 leading-relaxed" data-testid="text-product-description">
              {product.description}
            </p>
          )}

          {/* Size Selection */}
          {product.sizes && product.sizes.length > 1 && (
            <div>
              <h3 className="text-sm font-medium mb-3" data-testid="text-size-label">Size</h3>
              <Select value={selectedSize} onValueChange={setSelectedSize}>
                <SelectTrigger className="w-full" data-testid="select-size">
                  <SelectValue placeholder="Select size" />
                </SelectTrigger>
                <SelectContent>
                  {product.sizes.map((size) => (
                    <SelectItem key={size} value={size}>
                      {size}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          )}

          {/* Color Selection */}
          {product.colors && product.colors.length > 1 && (
            <div>
              <h3 className="text-sm font-medium mb-3" data-testid="text-color-label">Color</h3>
              <div className="flex flex-wrap gap-2">
                {product.colors.map((color) => (
                  <Button
                    key={color}
                    variant={selectedColor === color ? 'default' : 'outline'}
                    size="sm"
                    onClick={() => setSelectedColor(color)}
                    data-testid={`button-color-${color.toLowerCase()}`}
                  >
                    {color}
                  </Button>
                ))}
              </div>
            </div>
          )}

          {/* Quantity */}
          <div>
            <h3 className="text-sm font-medium mb-3" data-testid="text-quantity-label">Quantity</h3>
            <div className="flex items-center space-x-3">
              <Button
                variant="outline"
                size="sm"
                onClick={() => setQuantity(Math.max(1, quantity - 1))}
                disabled={quantity <= 1}
                data-testid="button-decrease-quantity"
              >
                <Minus size={16} />
              </Button>
              <span className="w-12 text-center" data-testid="text-quantity">
                {quantity}
              </span>
              <Button
                variant="outline"
                size="sm"
                onClick={() => setQuantity(quantity + 1)}
                data-testid="button-increase-quantity"
              >
                <Plus size={16} />
              </Button>
            </div>
          </div>

          {/* Add to Cart */}
          <div className="space-y-3">
            <Button
              className="w-full bg-black text-white hover:bg-gray-800 py-3"
              onClick={handleAddToCart}
              disabled={!product.inStock}
              data-testid="button-add-to-cart"
            >
              <ShoppingBag size={16} className="mr-2" />
              {product.inStock ? 'ADD TO CART' : 'OUT OF STOCK'}
            </Button>

            <Button
              variant="outline"
              className="w-full"
              onClick={() => setLiked(!liked)}
              data-testid="button-add-to-wishlist"
            >
              <Heart
                size={16}
                className={`mr-2 ${liked ? 'fill-red-500 text-red-500' : ''}`}
              />
              {liked ? 'ADDED TO WISHLIST' : 'ADD TO WISHLIST'}
            </Button>
          </div>

          {/* Stock Status */}
          {product.inStock && (
            <Badge variant="outline" className="text-green-600 border-green-200" data-testid="badge-in-stock">
              In Stock
            </Badge>
          )}

          {/* Additional Info */}
          <div className="space-y-4 pt-6 border-t border-gray-100">
            <div className="flex items-center space-x-3 text-sm text-gray-600">
              <Truck size={16} />
              <span>Free delivery on orders over â‚¹2,990</span>
            </div>
            <div className="flex items-center space-x-3 text-sm text-gray-600">
              <RotateCcw size={16} />
              <span>30-day return policy</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/ui/alert-dialog.ts.content:
import * as React from "react"
import * as AlertDialogPrimitive from "@radix-ui/react-alert-dialog"

import { cn } from "@/lib/utils"
import { buttonVariants } from "@/components/ui/button"

const AlertDialog = AlertDialogPrimitive.Root

const AlertDialogTrigger = AlertDialogPrimitive.Trigger

const AlertDialogPortal = AlertDialogPrimitive.Portal

const AlertDialogOverlay = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Overlay>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Overlay>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Overlay
    className={cn(
      "fixed inset-0 z-50 bg-black/80  data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0",
      className
    )}
    {...props}
    ref={ref}
  />
))
AlertDialogOverlay.displayName = AlertDialogPrimitive.Overlay.displayName

const AlertDialogContent = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Content>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Content>
>(({ className, ...props }, ref) => (
  <AlertDialogPortal>
    <AlertDialogOverlay />
    <AlertDialogPrimitive.Content
      ref={ref}
      className={cn(
        "fixed left-[50%] top-[50%] z-50 grid w-full max-w-lg translate-x-[-50%] translate-y-[-50%] gap-4 border bg-background p-6 shadow-lg duration-200 data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0 data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95 data-[state=closed]:slide-out-to-left-1/2 data-[state=closed]:slide-out-to-top-[48%] data-[state=open]:slide-in-from-left-1/2 data-[state=open]:slide-in-from-top-[48%] sm:rounded-lg",
        className
      )}
      {...props}
    />
  </AlertDialogPortal>
))
AlertDialogContent.displayName = AlertDialogPrimitive.Content.displayName

const AlertDialogHeader = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) => (
  <div
    className={cn(
      "flex flex-col space-y-2 text-center sm:text-left",
      className
    )}
    {...props}
  />
)
AlertDialogHeader.displayName = "AlertDialogHeader"

const AlertDialogFooter = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) => (
  <div
    className={cn(
      "flex flex-col-reverse sm:flex-row sm:justify-end sm:space-x-2",
      className
    )}
    {...props}
  />
)
AlertDialogFooter.displayName = "AlertDialogFooter"

const AlertDialogTitle = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Title>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Title>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Title
    ref={ref}
    className={cn("text-lg font-semibold", className)}
    {...props}
  />
))
AlertDialogTitle.displayName = AlertDialogPrimitive.Title.displayName

const AlertDialogDescription = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Description>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Description>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Description
    ref={ref}
    className={cn("text-sm text-muted-foreground", className)}
    {...props}
  />
))
AlertDialogDescription.displayName =
  AlertDialogPrimitive.Description.displayName

const AlertDialogAction = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Action>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Action>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Action
    ref={ref}
    className={cn(buttonVariants(), className)}
    {...props}
  />
))
AlertDialogAction.displayName = AlertDialogPrimitive.Action.displayName

const AlertDialogCancel = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Cancel>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Cancel>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Cancel
    ref={ref}
    className={cn(
      buttonVariants({ variant: "outline" }),
      "mt-2 sm:mt-0",
      className
    )}
    {...props}
  />
))
AlertDialogCancel.displayName = AlertDialogPrimitive.Cancel.displayName

export {
  AlertDialog,
  AlertDialogPortal,
  AlertDialogOverlay,
  AlertDialogTrigger,
  AlertDialogContent,
  AlertDialogHeader,
  AlertDialogFooter,
  AlertDialogTitle,
  AlertDialogDescription,
  AlertDialogAction,
  AlertDialogCancel,
}

